import { useState } from "react";
import styles from "./index.module.less";
import { Table } from "antd";
import cls from "classnames";

const InventoryManage = () => {
  const [step, setStep] = useState(1);
  const [activeBuild, setActiveBuild] = useState(0);
  const [activeUnit, setActiveUnit] = useState(0);
  const inventoryData = [
    {
      name: "1幢",
      id: 1,
    },
    {
      name: "2幢",
      id: 2,
    },
    {
      name: "3幢",
      id: 3,
    },
    {
      name: "4幢",
      id: 4,
    },
    {
      name: "5幢",
      id: 5,
    },
  ];
  const handleNext = () => {
    setStep(2);
  };
  const unitData = [
    {
      name: "一单元",
      code: "one",
    },
    {
      name: "二单元",
      code: "two",
    },
    {
      name: "三单元",
      code: "three",
    },
    {
      name: "四单元",
      code: "two",
    },
    {
      name: "五单元",
      code: "three",
    },
    {
      name: "六单元",
      code: "two",
    },
    {
      name: "七单元",
      code: "three",
    },
  ];
  const dataSource = [
    {
      key: 1,
      floor: "一层",
      unit1: "98.08",
      unit2: "88.09",
      unit3: "100.09",
      unit4: "120.09",
    },
    {
      key: 2,
      floor: "二层",
      unit1: "98.08",
      unit2: "88.09",
      unit3: "100.09",
      unit4: "120.09",
    },
  ];
  const columns = [
    {
      title: "楼层",
      dataIndex: "floor",
    },
    {
      title: "3室2厅2卫",
      dataIndex: "unit1",
    },
    {
      title: "3室2厅1卫",
      dataIndex: "unit2",
    },
    {
      title: "2室2厅1卫",
      dataIndex: "unit3",
    },
    {
      title: "4室2厅1卫",
      dataIndex: "unit4",
    },
  ];
  const handleChangeUnit = (index, code) => {
    setActiveUnit(index);
    console.log(code);
  };
  return (
    <>
      <div>库存管理</div>
      <div className={styles.wrapper}>
        {step === 1 &&
          inventoryData.map((item, index) => {
            return (
              <div
                className={cls(
                  styles.item,
                  activeBuild === index ? styles.active : ""
                )}
                onClick={handleNext}
                key={item.id}
              >
                {item.name}
              </div>
            );
          })}
        {step === 2 && (
          <div className={styles.secondStep}>
            <div className={styles.title}>
              {unitData.map((item, index) => {
                return (
                  <div
                    className={cls(
                      styles.item,
                      activeUnit === index ? styles.active : ""
                    )}
                    key={item.code}
                    onClick={() => handleChangeUnit(index, item.code)}
                  >
                    {item.name}
                  </div>
                );
              })}
            </div>
            <div>
              <Table
                columns={columns}
                dataSource={dataSource}
                // rowClassName={getClassName}
                bordered
                pagination={false}
                rowKey={(record) => record.key}
                onRow={(record) => {
                  return {
                    onClick: (event) => {
                      console.log(recode, "recode");
                    },
                  };
                }}
              ></Table>
            </div>
          </div>
        )}
      </div>
    </>
  );
};
export default InventoryManage;
