import { useEffect, useState } from "react";
import CalendarPanel from "./calander/index";
import Header from "../header";
import {
  Button,
  DatePicker,
  DatePickerProps,
  Form,
  Input,
  message,
} from "antd";
import styles from "./index.module.less";
import { RangePickerProps } from "antd/es/date-picker";
import {
  ISchedule,
  getSchedule,
  getScheduleById,
  postScheduleInfo,
} from "@/api/app";
import dayjs from "dayjs";
import FormBox from "./form";
const { TextArea } = Input;
type FieldType = {};
const dataFormat = 'YYYY-MM-DD HH:mm:ss"';
const ScheduleManage = (props) => {
  const { onhandleClose, id } = props;
  const [step, setStep] = useState(1);
  const [meetingList, setMeetingList] = useState<ISchedule[]>([]);
  const [editData, setEditData] = useState<ISchedule>();
  const [form] = Form.useForm();
  const title = "日程管理";
  const handleOk = (data) => {
    const othersData = {
      customerId: id,
      isAllDay: 0,
      startDate: dealTime(data.startDate),
      endDate: dealTime(data.endDate),
    };
    const newData = {
      ...data,
      ...othersData,
    };
    console.log(newData);
    addSchedule(newData);
  };
  const addSchedule = (data) => {
    postScheduleInfo(data).then((res) => {
      message.success("新增成功");
    });
  };
  const dealTime = (date) => {
    return dayjs(date).format("YYYY-MM-DD HH:mm:ss");
  };

  const handleDetail = (id) => {
    console.log(id);
    getScheduleById(id).then((res) => {
      console.log(res);
      setEditData(res[0]);

      setStep(2);
    });
  };

  const onhandleAdd = () => {
    // 新增事项
    setStep(2);
  };
  const getDayTime = (date) => {
    return {
      year: dayjs(date).year(),
      month: dayjs(date).month() + 1,
      day: dayjs(date).date(),
    };
  };
  useEffect(() => {
    const currentDate = {
      year: dayjs().year(),
      month: dayjs().month() + 1,
      day: dayjs().date(),
    };
    getScheduleList(id, currentDate);
  }, []);
  const getScheduleList = (id, date) => {
    getSchedule(id, date).then((res) => {
      setMeetingList(res);
    });
  };
  const changeTimeChoose = (time) => {
    getScheduleList(id, getDayTime(time));
  };
  const getMettingTime = (time) => {
    return (
      dayjs(time).hour() +
      ":" +
      (dayjs(time).minute() < 10
        ? "0" + dayjs(time).minute()
        : dayjs(time).minute())
    );
  };
  return (
    <div>
      {step === 1 && (
        <>
          <Header
            onhandleback={onhandleClose}
            title={title}
            onHandleAdd={onhandleAdd}
            type={"calendar"}
          />
          <CalendarPanel onChangeTime={changeTimeChoose} />
          <div className={styles.jobList}>
            <ul>
              {meetingList.length > 0
                ? meetingList.map((item, index) => {
                    return (
                      <li key={index} onClick={() => handleDetail(item.id)}>
                        <div className={styles.left}>
                          {getMettingTime(item.startDate)} <br />{" "}
                          <span>{getMettingTime(item.endDate)} </span>
                        </div>
                        <div>{item.title}</div>
                      </li>
                    );
                  })
                : null}
            </ul>
          </div>
        </>
      )}
      {step === 2 && (
        <>
          <Header
            onhandleback={() => setStep(1)}
            title={title}
            onhandleClose={onhandleClose}
          />
          <div className={styles.formBox}>
            <FormBox handleOk={handleOk} />
          </div>
        </>
      )}
    </div>
  );
};
export default ScheduleManage;
