import { useState } from "react";
import UserForm from "./form";
import styles from "./index.module.less";
import backTitle from "@/assets/images/backTitle.png";
import homeIcon from "@/assets/images/home.png";
import submitSucc from "@/assets/images/submitSuccess.png";
import Header from "../header";
const UserProtrait = (props) => {
  const { onhandleClose } = props;
  const [stepOne, setStepOne] = useState(1);
  const submitData = () => {
    setStepOne(2);
  };
  const handleUpdata = () => {
    setStepOne(1);
  };
  return (
    <>
      {stepOne === 1 ? (
        <>
          <div className={styles.headerWrapper}>
            <div className={styles.header}>
              <img
                className={styles.back}
                onClick={onhandleClose}
                src={backTitle}
                alt=""
              />
              <div>客户画像</div>
              <img
                onClick={onhandleClose}
                className={styles.homeIcon}
                src={homeIcon}
                alt=""
              />
            </div>
          </div>

          <div className={styles.userForm}>
            <UserForm onHandleBack={submitData} />
          </div>
        </>
      ) : (
        <>
          <Header
            onhandleback={() => setStepOne(1)}
            onhandleClose={onhandleClose}
          />
          <div className={styles.subwrapper}>
            <img className={styles.success} src={submitSucc} alt="" />
            <div className={styles.text}>提交成功</div>
            <div className={styles.update} onClick={handleUpdata}>
              继续更新
            </div>
          </div>
        </>
      )}
    </>
  );
};
export default UserProtrait;
