import { Button, Form, Input, Radio } from 'antd'
import advice from '@/assets/images/advice.png'
import styles from './index.module.less'
import { useEffect, useState } from 'react'
const { TextArea } = Input
type IProps = {
  onHandleBack: (data) => void
  data: unknown
  portrait: string
}
export type FieldType = {
  age: number
  email: string
  familyMembers: string
  gender: number
  intentEstate: string
  maritalStatus: number
  name: string
  occupation: string
  phone: string
  purchaseMotivation: string
}

const UserForm = (props: IProps) => {
  const { onHandleBack, data, portrait } = props
  const [textValue, setTextValue] = useState<string>('')
  const [form] = Form.useForm()
  const handleOk = (data) => {
    // 提交数据 去到下一步
    onHandleBack(data)
  }
  useEffect(() => {
    // const data = {
    //   name: '里斯',
    //   email: '7429@qq.com',
    // }
    setTextValue(handleProtrait(portrait))
  }, [portrait])
  const handleProtrait = (data) => {
    let textAreaValue = ''
    for (const key in data) {
      textAreaValue += `${data[key]}`
    }
    return textAreaValue
  }
  const handleForm = (data) => {
    for (const key in data) {
      form.setFieldsValue({
        [key]: data[key],
      })
    }
  }
  useEffect(() => {
    form.setFieldsValue(data)
  }, [data])
  const handleChange = (value) => {
    console.log(value)
  }
  const handleClick = () => {
    // const data = {
    //   name: '里斯',
    //   email: '7429@qq.com',
    // }
    // 处理数据一键填写表单
    handleForm(portrait)
  }
  return (
    <>
      <div className={styles.formWrapper}>
        <Form form={form} labelCol={{ span: 5 }} onFinish={handleOk}>
          <Form.Item<FieldType> label='姓名：' name='name'>
            <Input />
          </Form.Item>
          <Form.Item label='性别：' name='gender'>
            <Radio.Group>
              <Radio value={0}> 男 </Radio>
              <Radio value={1}> 女 </Radio>
            </Radio.Group>
          </Form.Item>
          <Form.Item<FieldType> label='年龄：' name='age'>
            <Input placeholder='请输入' />
          </Form.Item>
          <Form.Item<FieldType> label='职业：' name='occupation'>
            <Input />
          </Form.Item>
          <Form.Item<FieldType> label='手机：' name='phone'>
            <Input />
          </Form.Item>
          <Form.Item<FieldType> label='邮箱：' name='email'>
            <Input />
          </Form.Item>
          <Form.Item<FieldType> label='婚姻状况：' name='maritalStatus'>
            <Radio.Group>
              <Radio value={0}> 已婚 </Radio>
              <Radio value={1}> 单身 </Radio>
              <Radio value={2}>离婚 </Radio>
            </Radio.Group>
          </Form.Item>
          <Form.Item<FieldType> label='家庭成员：' name='familyMembers'>
            <Input />
          </Form.Item>
          <Form.Item<FieldType> label='意向楼盘：' name='intentEstate'>
            <Input disabled />
          </Form.Item>
          <Form.Item<FieldType> label='购房动机：' name='purchaseMotivation'>
            <Input />
          </Form.Item>

          <Form.Item style={{ marginBottom: 0 }}>
            <Button
              type='primary'
              htmlType='submit'
              style={{ margin: '0 auto', display: 'block' }}
            >
              提交
            </Button>
          </Form.Item>
        </Form>
      </div>

      <div className={styles.advice}>
        <div className={styles.adTitle}>
          <img src={advice} alt='' />
          <span>AI建议</span>
        </div>
        <div className={styles.bottom}>
          <TextArea
            rows={4}
            value={textValue}
            disabled
            onChange={handleChange}
          />
          <div className={styles.auto} onClick={handleClick}>
            一键填写
          </div>
        </div>
      </div>
    </>
  )
}
export default UserForm
