import { useState } from "react";
import CalendarPanel from "./calander/index";
import Header from "../header";
import { DatePicker, Form, Input } from "antd";
type FieldType = {};
const ScheduleManage = (props) => {
  const { onhandleClose } = props;
  const [step, setStep] = useState(1);
  const [form] = Form.useForm();
  const title = "日程管理";
  const handleOk = () => {};
  const onChangeTime = (time) => {
    console.log(time);
  };
  return (
    <div>
      {step === 1 && (
        <>
          <Header
            onhandleback={onhandleClose}
            title={title}
            onhandleClose={onhandleClose}
          />
          <CalendarPanel />
        </>
      )}
      {step === 2 && (
        <div>
          <Form form={form} labelCol={{ span: 5 }} onFinish={handleOk}>
            <Form.Item<FieldType> label="参与人" name="name">
              <Input placeholder="请输入" />
            </Form.Item>
            <Form.Item<FieldType> label="开始" name="startTime">
              <DatePicker picker={"time"} onChange={onChangeTime} />
            </Form.Item>
          </Form>
        </div>
      )}
    </div>
  );
};
export default ScheduleManage;
