import React, { useState } from "react";
import UserProtrait from "../userPortrait";
import InventoryManage from "../inventoryManage";

const MarktingPlugin = () => {
  const [showBase, setShowBase] = useState(true);
  const [showUser, setShowUser] = useState(false);
  // const [showSchedule, setShowSchedule] = useState(false);
  const [showInventory, setShowInventory] = useState(false);
  const handleCLoseUser = () => {
    setShowUser(false);
    setShowBase(true);
  };
  return (
    <div>
      {showBase && (
        <>
          <div
            onClick={() => {
              setShowBase(false);
              setShowUser(true);
            }}
          >
            用户画像
          </div>
          <div
            onClick={() => {
              setShowBase(false);
              setShowInventory(true);
            }}
          >
            库存管理
          </div>
          <div>日程管理</div>
        </>
      )}
      {showUser && <UserProtrait onhandleClose={handleCLoseUser} />}
      {showInventory && <InventoryManage />}
    </div>
  );
};
export default MarktingPlugin;
