import React, { useState } from "react";
import dayjs from "dayjs";
import "dayjs/locale/zh-cn";
import type { Dayjs } from "dayjs";
import dayLocaleData from "dayjs/plugin/localeData";
import { Calendar, Col, Radio, Row, Select, Typography, theme } from "antd";
import type { CalendarProps } from "antd";
import type { CellRenderInfo } from "rc-picker/es/interface";
import styles from "./index.module.less";
dayjs.extend(dayLocaleData);

const CalendarPanel: React.FC = () => {
  const { token } = theme.useToken();
  const [calenderValue, setCalenderValue] = useState<Dayjs>();
  const [validValue, setvalidValue] = useState<Dayjs[]>([
    dayjs("2023-11-07"),
    dayjs("2023-11-09"),
  ]);
  const onPanelChange = (value: Dayjs, mode: CalendarProps<Dayjs>["mode"]) => {
    console.log(value.format("YYYY-MM-DD"), mode);
  };

  const wrapperStyle: React.CSSProperties = {
    width: "100%",
    border: `1px solid ${token.colorBorderSecondary}`,
    borderRadius: token.borderRadiusLG,
  };
  const handleSetToday = () => {
    //设置为当前的日期
    const value = dayjs();
    console.log(value);
    setCalenderValue(value);
  };
  const handleChange = (day: Dayjs) => {
    console.log(day);
    console.log(day.format("YYYY-MM-DD"));
    setCalenderValue(day);
  };
  //   const handleFullCell = (current, today, info) => {
  //     console.log(current, today, info);
  //   };

  const style: React.CSSProperties = {
    border: `1px solid ${token.colorPrimary}`,
    borderRadius: 4,
    background: "#C5DEFC",
  };
  const handleFullCell = React.useCallback(
    (current: number | Dayjs, info: CellRenderInfo<Dayjs>) => {
      console.log(typeof current, "current");
      console.log(info, "info");
      if (info.type !== "date") {
        return info.originNode;
      }
      if (typeof current === "number") {
        return <div className="ant-picker-cell-inner">{current}</div>;
      }
      return (
        <div
          //   className="ant-picker-cell-inner"
          className={cls(
            "ant-picker-cell-inner",
            urrent.date() === 1 ? styles.active : ""
          )}
        >
          {current.date()}
        </div>
      );
    },
    []
  );
  return (
    <div style={wrapperStyle} className={styles.calendarWrapper}>
      <Calendar
        value={calenderValue}
        fullscreen={false}
        headerRender={({ value, type, onChange, onTypeChange }) => {
          const start = 0;
          const end = 12;
          const monthOptions: any = [];

          const months: any = [];
          for (let i = 0; i < 12; i++) {
            months.push(i + 1);
          }

          for (let i = start; i < end; i++) {
            monthOptions.push(
              <Select.Option key={i} value={i} className="month-item">
                {months[i] + "月"}
              </Select.Option>
            );
          }

          const year = value.year();
          const month = value.month();
          const options: any = [];
          for (let i = year - 10; i < year + 10; i += 1) {
            options.push(
              <Select.Option key={i} value={i} className="year-item">
                {i}
              </Select.Option>
            );
          }
          return (
            <div style={{ padding: 8 }}>
              <Row gutter={8}>
                <Col>
                  <Select
                    size="small"
                    popupMatchSelectWidth={false}
                    className="my-year-select"
                    value={year}
                    onChange={(newYear) => {
                      const now = value.clone().year(newYear);
                      onChange(now);
                    }}
                  >
                    {options}
                  </Select>
                </Col>
                <Col>
                  <Select
                    size="small"
                    popupMatchSelectWidth={false}
                    value={month}
                    onChange={(newMonth) => {
                      const now = value.clone().month(newMonth);
                      onChange(now);
                    }}
                  >
                    {monthOptions}
                  </Select>
                </Col>
                <Col>
                  <div onClick={handleSetToday}>今天</div>
                </Col>
              </Row>
            </div>
          );
        }}
        onPanelChange={onPanelChange}
        onChange={handleChange}
        fullCellRender={(current, info) => handleFullCell(current, info)}
      />
    </div>
  );
};

export default CalendarPanel;
