import { useEffect, useState } from "react";
import CalendarPanel from "./calander/index";
import Header from "../header";
import {
  Button,
  DatePicker,
  DatePickerProps,
  Form,
  Input,
  message,
} from "antd";
import styles from "./index.module.less";
import { RangePickerProps } from "antd/es/date-picker";
import {
  ISchedule,
  getSchedule,
  getScheduleById,
  postScheduleInfo,
} from "@/api/app";
import dayjs from "dayjs";
const { TextArea } = Input;
type FieldType = {};
const ScheduleManage = (props) => {
  const { onhandleClose, id } = props;
  const [step, setStep] = useState(1);
  const [meetingList, setMeetingList] = useState<ISchedule[]>([]);
  const [form] = Form.useForm();
  const title = "日程管理";
  const handleOk = (data) => {
    const othersData = {
      customerId: id,
      isAllDay: 0,
      startDate: dealTime(data.startDate),
      endDate: dealTime(data.endDate),
    };
    const newData = {
      ...data,
      ...othersData,
    };
    console.log(newData);
    addSchedule(newData);
  };
  const addSchedule = (data) => {
    postScheduleInfo(data).then((res) => {
      message.success("新增成功");
    });
  };
  const dealTime = (date) => {
    return dayjs(date).format("YYYY-MM-DD HH:mm:ss");
  };
  const onChangeTime = (time) => {
    console.log(time);
  };
  const handleDetail = (id) => {
    console.log(id);
    getScheduleById(id).then((res) => {
      console.log(res);
      form.setFieldsValue(res);
      setStep(2);
    });
  };

  const onChangeStartTime = (
    value: DatePickerProps["value"] | RangePickerProps["value"],
    dateString: [string, string] | string
  ) => {
    console.log("Selected Time: ", value);
    console.log("Formatted Selected Time: ", dateString);
  };

  const onOkStart = (
    value: DatePickerProps["value"] | RangePickerProps["value"]
  ) => {
    console.log("onOk: ", value);
  };
  const onChangeEndTime = (
    value: DatePickerProps["value"] | RangePickerProps["value"],
    dateString: [string, string] | string
  ) => {
    console.log("Selected Time: ", value);
    console.log("Formatted Selected Time: ", dateString);
  };

  const onOkEnd = (
    value: DatePickerProps["value"] | RangePickerProps["value"]
  ) => {
    console.log("onOk: ", value);
  };
  const onhandleAdd = () => {
    // 新增事项
    setStep(2);
  };
  const getDayTime = (date) => {
    return {
      year: dayjs(date).year(),
      month: dayjs(date).month() + 1,
      day: dayjs(date).date(),
    };
  };
  useEffect(() => {
    const currentDate = {
      year: dayjs().year(),
      month: dayjs().month() + 1,
      day: dayjs().date(),
    };
    getScheduleList(id, currentDate);
  }, []);
  const getScheduleList = (id, date) => {
    getSchedule(id, date).then((res) => {
      setMeetingList(res);
    });
  };
  const changeTimeChoose = (time) => {
    getScheduleList(id, getDayTime(time));
  };
  const getMettingTime = (time) => {
    return (
      dayjs(time).hour() +
      ":" +
      (dayjs(time).minute() < 10
        ? "0" + dayjs(time).minute()
        : dayjs(time).minute())
    );
  };
  return (
    <div>
      {step === 1 && (
        <>
          <Header
            onhandleback={onhandleClose}
            title={title}
            onHandleAdd={onhandleAdd}
            type={"calendar"}
          />
          <CalendarPanel onChangeTime={changeTimeChoose} />
          <div className={styles.jobList}>
            <ul>
              {meetingList.length > 0
                ? meetingList.map((item, index) => {
                    return (
                      <li key={index} onClick={() => handleDetail(item.id)}>
                        <div className={styles.left}>
                          {getMettingTime(item.startDate)} <br />{" "}
                          <span>{getMettingTime(item.endDate)} </span>
                        </div>
                        <div>{item.title}</div>
                      </li>
                    );
                  })
                : null}
            </ul>
          </div>
        </>
      )}
      {step === 2 && (
        <>
          <Header
            onhandleback={() => setStep(1)}
            title={title}
            onhandleClose={onhandleClose}
          />
          <div className={styles.formBox}>
            <Form form={form} labelCol={{ span: 5 }} onFinish={handleOk}>
              <Form.Item<FieldType> label="参与人：" name="attendees">
                <Input placeholder="请输入" />
              </Form.Item>
              <Form.Item<FieldType> label="主题：" name="title">
                <Input placeholder="请输入" />
              </Form.Item>
              <Form.Item<FieldType> label="开始时间：" name="startDate">
                <DatePicker
                  showTime
                  onChange={onChangeStartTime}
                  onOk={onOkStart}
                  placeholder="请输入"
                />
              </Form.Item>
              <Form.Item<FieldType> label="结束时间：" name="endDate">
                <DatePicker
                  showTime
                  onChange={onChangeEndTime}
                  onOk={onOkEnd}
                  placeholder="请输入"
                />
              </Form.Item>
              <Form.Item<FieldType> label="地点：" name="location">
                <Input placeholder="添加地点" />
              </Form.Item>
              <Form.Item<FieldType> label="描述：" name="description">
                <TextArea rows={4} placeholder="请输入" />
              </Form.Item>
              <Form.Item style={{ marginBottom: 0 }}>
                <Button
                  type="primary"
                  htmlType="submit"
                  style={{ margin: "0 auto", display: "block" }}
                >
                  提交
                </Button>
              </Form.Item>
            </Form>
          </div>
        </>
      )}
    </div>
  );
};
export default ScheduleManage;
