import { useEffect, useState } from "react";
import CalendarPanel from "./calander/index";
import Header from "../header";
import {
  Button,
  DatePicker,
  DatePickerProps,
  Form,
  Input,
  message,
} from "antd";
import styles from "./index.module.less";
import { RangePickerProps } from "antd/es/date-picker";
import { getSchedule, postScheduleInfo } from "@/api/app";
import dayjs from "dayjs";
const { TextArea } = Input;
type FieldType = {};
const ScheduleManage = (props) => {
  const { onhandleClose, id } = props;
  const [step, setStep] = useState(1);
  const [form] = Form.useForm();
  const title = "日程管理";
  const handleOk = (data) => {
    const othersData = {
      customerId: id,
      isAllDay: 0,
      startDate: dealTime(data.startDate),
      endDate: dealTime(data.startDate),
    };
    console.log(dealTime(data.startTime));
    console.log(data, "submit");
    const newData = {
      ...data,
      ...othersData,
    };
    addSchedule(newData);
  };
  const addSchedule = (data) => {
    postScheduleInfo(data).then((res) => {
      message.success("新增成功");
    });
  };
  const dealTime = (date) => {
    return dayjs(date).format("YYYY-MM-DD HH:mm:ss");
  };
  const onChangeTime = (time) => {
    console.log(time);
  };
  const handleDetail = () => {
    setStep(2);
  };

  const onChangeStartTime = (
    value: DatePickerProps["value"] | RangePickerProps["value"],
    dateString: [string, string] | string
  ) => {
    console.log("Selected Time: ", value);
    console.log("Formatted Selected Time: ", dateString);
  };

  const onOkStart = (
    value: DatePickerProps["value"] | RangePickerProps["value"]
  ) => {
    console.log("onOk: ", value);
  };
  const onChangeEndTime = (
    value: DatePickerProps["value"] | RangePickerProps["value"],
    dateString: [string, string] | string
  ) => {
    console.log("Selected Time: ", value);
    console.log("Formatted Selected Time: ", dateString);
  };

  const onOkEnd = (
    value: DatePickerProps["value"] | RangePickerProps["value"]
  ) => {
    console.log("onOk: ", value);
  };
  const onhandleAdd = () => {
    // 新增事项
    setStep(2);
  };
  useEffect(() => {
    const currentDate = {
      year: dayjs().year(),
      month: dayjs().month() + 1,
      day: dayjs().date(),
    };
    getScheduleList(id, currentDate);
  }, []);
  const getScheduleList = (id, date) => {
    getSchedule(id, date).then((res) => {
      console.log(res);
    });
  };
  return (
    <div>
      {step === 1 && (
        <>
          <Header
            onhandleback={onhandleClose}
            title={title}
            onHandleAdd={onhandleAdd}
            type={"calendar"}
          />
          <CalendarPanel />
          <div className={styles.jobList}>
            <ul>
              <li onClick={handleDetail}>
                <div className={styles.left}>
                  10:00 <br /> <span>11:00</span>
                </div>
                <div>安全产品复盘-305</div>
              </li>
              <li>
                <div className={styles.left}>
                  10:00 <br /> <span>11:00</span>
                </div>
                <div>安全产品复盘-305</div>
              </li>
              <li>
                <div className={styles.left}>
                  10:00 <br /> <span>11:00</span>
                </div>
                <div>安全产品复盘-305</div>
              </li>
            </ul>
          </div>
        </>
      )}
      {step === 2 && (
        <>
          <Header
            onhandleback={() => setStep(1)}
            title={title}
            onhandleClose={onhandleClose}
          />
          <div className={styles.formBox}>
            <Form form={form} labelCol={{ span: 5 }} onFinish={handleOk}>
              <Form.Item<FieldType> label="参与人：" name="name">
                <Input placeholder="请输入" />
              </Form.Item>
              <Form.Item<FieldType> label="主题：" name="title">
                <Input placeholder="请输入" />
              </Form.Item>
              <Form.Item<FieldType> label="开始时间：" name="startDate">
                <DatePicker
                  showTime
                  onChange={onChangeStartTime}
                  onOk={onOkStart}
                  placeholder="请输入"
                />
              </Form.Item>
              <Form.Item<FieldType> label="结束时间：" name="endDate">
                <DatePicker
                  showTime
                  onChange={onChangeEndTime}
                  onOk={onOkEnd}
                  placeholder="请输入"
                />
              </Form.Item>
              <Form.Item<FieldType> label="地点：" name="location">
                <Input placeholder="添加地点" />
              </Form.Item>
              <Form.Item<FieldType> label="描述：" name="description">
                <TextArea rows={4} placeholder="请输入" />
              </Form.Item>
              <Form.Item style={{ marginBottom: 0 }}>
                <Button
                  type="primary"
                  htmlType="submit"
                  style={{ margin: "0 auto", display: "block" }}
                >
                  提交
                </Button>
              </Form.Item>
            </Form>
          </div>
        </>
      )}
    </div>
  );
};
export default ScheduleManage;
