import { useEffect, useState } from "react";
import styles from "./index.module.less";
import { Table } from "antd";
import cls from "classnames";
import Header from "../header";
import { IBuilds, getBuilding, getHouse, getHouseUnit } from "@/api/app";

const InventoryManage = (props) => {
  const { onhandleClose } = props;
  const [step, setStep] = useState(1);
  const [activeBuild, setActiveBuild] = useState(0);
  const [activeUnit, setActiveUnit] = useState(0);
  const [programData, setProgramData] = useState<IBuilds[]>([]);
  const [builds, setBuilds] = useState<IBuilds[]>([]);
  const [unitData, setUnitData] = useState<IBuilds[]>([]);
  const [currentBuild, setCurrentBuild] = useState<string>("");
  const [currentUnit, setCurrentUnit] = useState<string>("");
  const [unitWidth, setUnitWidth] = useState(0);
  const title = "库存管理";

  const handleNext = (code) => {
    const units = programData.filter((item) => item.parentCode === code);
    setCurrentBuild(code);
    console.log(units, "units");
    setCurrentUnit(units[0].code);
    setUnitData(units);
    setUnitWidth(138 * units.length);
    getHouseInfo(code, units[0].code); // 获取房子信息
    getHouseUnits(code, units[0].code);
    setStep(2);
  };
  const copydata = [
    {
      id: 2,
      name: "项目P1",
      code: "p1",
      parentCode: null,
    },
    {
      id: 3,
      name: "楼栋b1",
      code: "b1",
      parentCode: "p1",
    },
    {
      id: 4,
      name: "单元u1",
      code: "u1",
      parentCode: "b1",
    },
    {
      id: 5,
      name: "楼层f1",
      code: "f1",
      parentCode: "u1",
    },
    {
      id: 6,
      name: "楼层f2",
      code: "f2",
      parentCode: "u1",
    },
    {
      id: 7,
      name: "楼层f3",
      code: "f3",
      parentCode: "u1",
    },
    {
      id: 8,
      name: "单元u2",
      code: "u2",
      parentCode: "b1",
    },
    {
      id: 9,
      name: "单元u3",
      code: "u3",
      parentCode: "b1",
    },
    {
      id: 10,
      name: "楼栋b2",
      code: "b2",
      parentCode: "p1",
    },
  ];
  const unitArray = ["两房一厅一卫", "三房一厅两卫"];
  const housedata = [
    {
      floor: "楼层f1",
      houseSimple: [
        {
          id: 2,
          apartmentStructure: "1房1厅1卫",
          area: "100.00",
          subscriptionPrice: 100000,
          saleStatus: 0,
        },
        {
          id: 3,
          apartmentStructure: "三房一厅两卫",
          area: "100.00",
          subscriptionPrice: 100000,
          saleStatus: 0,
        },
      ],
    },
    {
      floor: "楼层f2",
      houseSimple: [],
    },
    {
      floor: "楼层f3",
      houseSimple: [],
    },
  ];
  const dataSource = [
    {
      key: 1,
      floor: "一层",
      unit1: "98.08",
      unit2: "88.32",
      unit3: "100.09",
      unit4: "120.09",
    },
    {
      key: 2,
      floor: "二层",
      unit1: "98.08",
      unit2: "88.09",
      unit3: "100.09",
      unit4: "120.09",
    },
  ];

  const columns = [
    {
      title: "楼层",
      dataIndex: "floor",
    },
    {
      title: "3室2厅2卫",
      dataIndex: "unit1",
    },
    {
      title: "3室2厅1卫",
      dataIndex: "unit2",
      render: (_, recode) => (
        <div onClick={() => test(recode)}>{recode.unit2}</div>
      ),
    },
    {
      title: "2室2厅1卫",
      dataIndex: "unit3",
    },
    {
      title: "4室2厅1卫",
      dataIndex: "unit4",
    },
  ];
  useEffect(() => {
    getBuildings();
  }, []);
  const getHouseUnits = (code, unit) => {
    getHouseUnit(code, unit).then((res) => {
      console.log(res, "户型");
    });
  };
  const getBuildings = () => {
    // setProgramData(copydata);
    // filterBuilds(copydata);
    getBuilding().then((res) => {
      console.log(res, "build");
      setProgramData(res);
      filterBuilds(res);
    });
  };
  const filterBuilds = (data) => {
