import { useState } from "react";
import styles from "./index.module.less";
import { Table } from "antd";
const InventoryManage = () => {
  const [step, setStep] = useState(1);

  const inventoryData = [
    {
      name: "1幢",
      id: 1,
    },
    {
      name: "2幢",
      id: 2,
    },
    {
      name: "3幢",
      id: 3,
    },
    {
      name: "4幢",
      id: 4,
    },
    {
      name: "5幢",
      id: 5,
    },
  ];
  const handleNext = () => {
    setStep(2);
  };
  const unitData = [
    {
      name: "一单元",
      code: "one",
    },
    {
      name: "二单元",
      code: "two",
    },
    {
      name: "三单元",
      code: "three",
    },
    {
      name: "四单元",
      code: "two",
    },
    {
      name: "五单元",
      code: "three",
    },
    {
      name: "四单元",
      code: "two",
    },
    {
      name: "五单元",
      code: "three",
    },
  ];
  const dataSource = [
    {
      key: 1,
      floor: "一层",
      unit1: "98.08",
      unit2: "88.09",
      unit3: "100.09",
      unit4: "120.09",
    },
    {
      key: 2,
      floor: "二层",
      unit1: "98.08",
      unit2: "88.09",
      unit3: "100.09",
      unit4: "120.09",
    },
  ];
  const columns = [
    {
      title: "楼层",
      dataIndex: "floor",
    },
    {
      title: "3室2厅2卫",
      dataIndex: "unit1",
    },
    {
      title: "3室2厅1卫",
      dataIndex: "unit2",
    },
    {
      title: "2室2厅1卫",
      dataIndex: "unit3",
    },
    {
      title: "4室2厅1卫",
      dataIndex: "unit4",
    },
  ];
  const columnss = [
    {
      id: 2,
      name: "测试房屋",
      code: "h1",
      floorCode: "f1",
      unitCode: "ul",
      buildingCode: "b1",
      projectCode: "p1",
      apartmentStructure: "1房1厅1卫",
      uinnerArea: "90.00",
      area: "100.00",
      bonusArea: "10.00",
      subscriptionPrice: 100000,
      totalSubscriptionPrice: 1000000,
      houseTypeName: "住宅-超高层",
      saleStatus: 0,
    },
    {
      wd: 3,
      name: "测试房屋",
      code: "h1",
      floorCode: "f1",
      unitCode: "ul",
      buildingCode: "b1",
      projectCode: "p1",
      apartmentStructure: "1房1厅1卫",
      uinnerArea: "90.00",
      area: "100.00",
      bonusArea: "10.00",
      subscriptionPrice: 100000,
      totalSubscriptionPrice: 1000000,
      houseTypeName: "住宅-超高层",
      saleStatus: 0,
    },
  ];

  return (
    <>
      <div>库存管理</div>
      <div className={styles.wrapper}>
        {step === 1 &&
          inventoryData.map((item) => {
            return (
              <div className={styles.item} onClick={handleNext} key={item.id}>
                {item.name}
              </div>
            );
          })}
        {step === 2 && (
          <div className={styles.secondStep}>
            <div className={styles.title}>
              {unitData.map((item) => {
                return (
                  <div className={styles.item} key={item.code}>
                    {item.name}
                  </div>
                );
              })}
            </div>
            <div>
              <Table
                columns={columns}
                dataSource={dataSource}
                // rowClassName={getClassName}
                bordered
                pagination={false}
                rowKey={(record) => record.key}
                scroll={{ x: "max-content" }}
              ></Table>
            </div>
          </div>
        )}
      </div>
    </>
  );
};
export default InventoryManage;
