import React, { useState } from "react";
import UserProtrait from "../userPortrait";
const MarktingPlugin = () => {
  const [showBase, setShowBase] = useState(true);
  const showUser = () => {
    setShowBase(false);
  };
  return (
    <div>
      {showBase ? (
        <>
          <div onClick={showUser}>用户画像</div>
          <div>库存管理</div>
          <div>日程管理</div>
        </>
      ) : (
        <UserProtrait />
      )}
    </div>
  );
};
export default MarktingPlugin;
