import { useEffect, useState } from "react";
import styles from "./index.module.less";
import { Table } from "antd";
import cls from "classnames";
import Header from "../header";
import { IBuilds, getBuilding } from "@/api/app";

const InventoryManage = (props) => {
  const { onhandleClose } = props;
  const [step, setStep] = useState(1);
  const [activeBuild, setActiveBuild] = useState(0);
  const [activeUnit, setActiveUnit] = useState(0);
  const [programData, setProgramData] = useState<IBuilds[]>();
  const [builds, setBuilds] = useState<IBuilds[]>();
  const [unitData, setUnitData] = useState<IBuilds[]>();
  const title = "库存管理";

  const handleNext = (code) => {
    const units = programData?.filter((item) => item.parentCode === code);
    setUnitData(units);
    setStep(2);
  };

  const dataSource = [
    {
      key: 1,
      floor: "一层",
      unit1: "98.08",
      unit2: "88.32",
      unit3: "100.09",
      unit4: "120.09",
    },
    {
      key: 2,
      floor: "二层",
      unit1: "98.08",
      unit2: "88.09",
      unit3: "100.09",
      unit4: "120.09",
    },
  ];
  const columns = [
    {
      title: "楼层",
      dataIndex: "floor",
    },
    {
      title: "3室2厅2卫",
      dataIndex: "unit1",
    },
    {
      title: "3室2厅1卫",
      dataIndex: "unit2",
      render: (_, recode) => (
        <div onClick={() => test(recode)}>{recode.unit2}</div>
      ),
    },
    {
      title: "2室2厅1卫",
      dataIndex: "unit3",
    },
    {
      title: "4室2厅1卫",
      dataIndex: "unit4",
    },
  ];
  useEffect(() => {
    getBuildings();
  }, []);
  const getBuildings = () => {
    getBuilding().then((res) => {
      console.log(res, "build");
      setProgramData(res);
      filterBuilds(res);
    });
  };
  const filterBuilds = (data) => {
    // 过滤出 parentCode 相同的数据
    const code = "p1";
    const builds = data.filter((item) => {
      return item.parentCode === code;
    });
    setBuilds(builds);
    console.log(builds);
  };
  const handleChangeUnit = (index, code) => {
    setActiveUnit(index);
    console.log(code);
  };
  const test = (value) => {
    console.log(value);
    setStep(3);
  };
  return (
    <>
      <div className={styles.wrapper}>
        {step === 1 && (
          <>
            <Header
              onhandleback={onhandleClose}
              title={title}
              onhandleClose={onhandleClose}
            />
            <div className={styles.inventoryWrapper}>
              {builds?.map((item, index) => {
                return (
                  <div
                    className={cls(
                      styles.item,
                      activeBuild === index ? styles.active : ""
                    )}
                    onClick={() => handleNext(item.code)}
                    key={item.id}
                  >
                    {item.name}
                  </div>
                );
              })}
            </div>
          </>
        )}
        <div>
          {step === 2 && (
            <div className={styles.secondStep}>
              <Header
                onhandleback={() => setStep(1)}
                title={title}
                onhandleClose={onhandleClose}
              />
              <div className={styles.title}>
                {unitData.map((item, index) => {
                  return (
                    <div
                      className={cls(
                        styles.item,
                        activeUnit === index ? styles.active : ""
                      )}
                      key={item.code}
                      onClick={() => handleChangeUnit(index, item.code)}
                    >
                      {item.name}
                    </div>
                  );
                })}
              </div>
              <div className={styles.table}>
                <Table
                  columns={columns}
                  dataSource={dataSource}
                  // rowClassName={getClassName}
                  bordered
                  pagination={false}
                  rowKey={(record) => record.key}
                  // onRow={(record, index) => {
                  //   return {
                  //     onClick: (event) => {
                  //       console.log(record, index, "recode");
                  //     },
                  //   };
                  // }}
                ></Table>
              </div>
            </div>
          )}
        </div>
        <div>
          {step === 3 && (
            <div className={styles.thirdStep}>
              <Header
                onhandleback={() => setStep(2)}
                title={title}
                onhandleClose={onhandleClose}
              />
              <ul className={styles.list}>
                <li>
                  <span>项目名称：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>产品类型：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>房源信息：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>房屋结构：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>房屋房型：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>房屋朝向：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>建筑面积：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>套内面积：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>赠送面积：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>建筑单价：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>套内单价：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>标准总价：</span> 招商蛇口深圳公司
                </li>
                <li>
                  <span>房源底价：</span> 招商蛇口深圳公司
                </li>
              </ul>
            </div>
          )}
        </div>
      </div>
    </>
  );
};
export default InventoryManage;
