import { useState } from "react";
import CalendarPanel from "./calander/index";
import Header from "../header";
import { Button, DatePicker, Form, Input } from "antd";
import styles from "./index.module.less";
const { RangePicker } = DatePicker;
const { TextArea } = Input;
type FieldType = {};
const ScheduleManage = (props) => {
  const { onhandleClose } = props;
  const [step, setStep] = useState(1);
  const [form] = Form.useForm();
  const title = "日程管理";
  const handleOk = () => {};
  const onChangeTime = (time) => {
    console.log(time);
  };
  return (
    <div>
      {step === 1 && (
        <>
          <Header
            onhandleback={onhandleClose}
            title={title}
            onhandleClose={onhandleClose}
          />
          <CalendarPanel />
          <div className={styles.jobList}>
            <ul>
              <li>
                <div className={styles.left}>
                  10:00 <br /> 11:00
                </div>
                <div>安全产品复盘-305</div>
              </li>
              <li>
                <div className={styles.left}>
                  10:00 <br /> 11:00
                </div>
                <div>安全产品复盘-305</div>
              </li>
              <li>
                <div className={styles.left}>
                  10:00 <br /> 11:00
                </div>
                <div>安全产品复盘-305</div>
              </li>
            </ul>
          </div>
        </>
      )}
      {step === 2 && (
        <div>
          <Form form={form} labelCol={{ span: 5 }} onFinish={handleOk}>
            <Form.Item<FieldType> label="参与人" name="name">
              <Input placeholder="请输入" />
            </Form.Item>
            <Form.Item<FieldType> label="时间" name="startTime">
              <RangePicker showTime />
            </Form.Item>
            <Form.Item<FieldType> label="地点" name="place">
              <Input placeholder="添加地点" />
            </Form.Item>
            <Form.Item<FieldType> label="描述" name="place">
              <TextArea rows={4} placeholder="请输入" />
            </Form.Item>
            <Form.Item style={{ marginBottom: 0 }}>
              <Button
                type="primary"
                htmlType="submit"
                style={{ margin: "0 auto", display: "block" }}
              >
                提交
              </Button>
            </Form.Item>
          </Form>
        </div>
      )}
    </div>
  );
};
export default ScheduleManage;
